mapper.insert:




	SELECT LAST_INSERT_ID()



INSERT INTO goods (
	goods_type_no
	, goods_type2_no
	, goods_name
	, goods_price
	, goods_active
) VALUE (
	#{goodsTypeNo}
	, #{goodsType2No}
	, #{goodsName}
	, #{goodsPrice}
	, #{goodsActive}
)

mapper.select:
SELECT goods_no goodsNo , goods_type_no goodsTypeNo , goods_type2_no goodsType2No , goods_name goodsName , goods_price goodsPrice , goods_active goodsActive , createdate FROM goods
#{searchType} like CONCAT('%', #{searchWord}, '%')
ORDER BY emp_no DESC LIMIT #{beginRow}, #{rowPerPage}
